<script>
export default {
  data(){ 
    return {
      isStreched: false,
    };
  },
  methods: {
    strechNav(isStreched) {
      const nav = document.querySelector('nav');
      const toggleArrow = document.getElementById('toggleNav');
      const ulLi = document.querySelectorAll('li')
      if(isStreched === false){
        this.isStreched = true;
        nav.style.width = '300px';
        ulLi.forEach(li => {
          li.style.justifyContent = 'left';
        }); 
        toggleArrow.classList.add('active')
      }else{
        this.isStreched = false;
        nav.style.width = '80px';
        ulLi.forEach(li => {
          li.style.justifyContent = 'center';
        }); 
        toggleArrow.classList.remove('active')
      }
      
    },
  },
}
</script>

